import React, { Component } from "react";
import apiKeys from "./apiKeys";
import Clock from "react-live-clock";
import Forcast from "./forcast";
import loader from "./images/WeatherIcons.gif";
import ReactAnimatedWeather from "react-animated-weather";

const dateBuilder = (d) => {
  const months = [
    "January", "February", "March", "April", "May", "June", "July",
    "August", "September", "October", "November", "December"
  ];
  const days = ["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"];

  let day = days[d.getDay()];
  let date = d.getDate();
  let month = months[d.getMonth()];
  let year = d.getFullYear();

  return `${day}, ${date} ${month} ${year}`;
};

const defaults = {
  color: "white",
  size: 112,
  animate: true,
};

class Weather extends Component {
  state = {
    lat: undefined,
    lon: undefined,
    errorMessage: undefined,
    weather: null,
    loading: true,
    icon: "CLEAR_DAY",
  };

  componentDidMount() {
    if (navigator.geolocation) {
      this.getPosition()
        .then((position) => {
          this.getWeather(position.coords.latitude, position.coords.longitude);
        })
        .catch(() => {
          // Use fallback if location access is denied
          this.getWeather(28.67, 77.22); // Default location
          alert("Location access denied. Showing weather for a default location.");
        });
    } else {
      alert("Geolocation not available.");
    }

    this.timerID = setInterval(
      () => this.getWeather(this.state.lat, this.state.lon),
      600000 // Update every 10 minutes
    );
  }

  componentWillUnmount() {
    clearInterval(this.timerID);
  }

  getPosition = () => {
    return new Promise((resolve, reject) => {
      navigator.geolocation.getCurrentPosition(resolve, reject);
    });
  };

  getWeather = async (lat, lon) => {
    try {
      const api_call = await fetch(
        `${apiKeys.base}weather?lat=${lat}&lon=${lon}&units=metric&APPID=${apiKeys.key}`
      );
      const data = await api_call.json();
      this.setState({
        lat,
        lon,
        weather: {
          city: data.name,
          temperatureC: Math.round(data.main.temp),
          temperatureF: Math.round(data.main.temp * 1.8 + 32),
          humidity: data.main.humidity,
          description: data.weather[0].description,
          country: data.sys.country,
        },
        icon: this.getWeatherIcon(data.weather[0].main),
        loading: false,
      });
    } catch (error) {
      this.setState({ errorMessage: "Failed to fetch weather data.", loading: false });
    }
  };

  getWeatherIcon(main) {
    switch (main) {
      case "Haze": return "CLEAR_DAY";
      case "Clouds": return "CLOUDY";
      case "Rain": return "RAIN";
      case "Snow": return "SNOW";
      case "Dust": return "WIND";
      case "Drizzle": return "SLEET";
      case "Fog": return "FOG";
      case "Smoke": return "FOG";
      case "Tornado": return "WIND";
      default: return "CLEAR_DAY";
    }
  }

  render() {
    const { weather, loading, icon, errorMessage } = this.state;

    if (loading) {
      return (
        <div>
          <img src={loader} alt="Loading" style={{ width: "50%" }} />
          <h3 style={{ color: "white", fontSize: "22px", fontWeight: "600" }}>
            Detecting your location
          </h3>
        </div>
      );
    }

    if (errorMessage) {
      return <h3 style={{ color: "red" }}>{errorMessage}</h3>;
    }

    return (
      <div className="city">
        <div className="title">
          <h2>{weather.city}</h2>
          <h3>{weather.country}</h3>
        </div>
        <div className="mb-icon">
          <ReactAnimatedWeather
            icon={icon}
            color={defaults.color}
            size={defaults.size}
            animate={defaults.animate}
          />
          <p>{weather.description}</p>
        </div>
        <div className="date-time">
          <div className="dmy">
            <div className="current-time">
              <Clock format="HH:mm:ss" interval={1000} ticking={true} />
            </div>
            <div className="current-date">{dateBuilder(new Date())}</div>
          </div>
          <div className="temperature">
            <p>{weather.temperatureC}Â°<span>C</span></p>
          </div>
        </div>
        <Forcast icon={icon} weather={weather.description} />
      </div>
    );
  }
}

export default Weather;
