import React, { useState, useEffect } from "react";
import axios from "axios";
import ReactAnimatedWeather from "react-animated-weather";

const GOOGLE_API_KEY = "AIzaSyDjag9OAcGwC5-wIHxs5it8BgSUwAZKhOg"; // Replace with your Google API key
const OPENWEATHER_API_KEY = "YOUR_OPENWEATHERMAP_API_KEY"; // Replace with your OpenWeatherMap API key

function Forcast() {
  const [query, setQuery] = useState("");
  const [error, setError] = useState("");
  const [weather, setWeather] = useState({});

  // Search function using Google Geocoding API for more accurate location data
  const search = (location) => {
    // Step 1: Get the coordinates using Google Geocoding API
    axios
      .get(
        `https://maps.googleapis.com/maps/api/geocode/json?address=${location}&key=${GOOGLE_API_KEY}`
      )
      .then((geoResponse) => {
        if (geoResponse.data.results.length > 0) {
          const { lat, lng } = geoResponse.data.results[0].geometry.location;

          // Step 2: Use those coordinates to get weather data from OpenWeatherMap
          axios
            .get(
              `https://api.openweathermap.org/data/2.5/weather?lat=${lat}&lon=${lng}&units=metric&appid=${OPENWEATHER_API_KEY}`
            )
            .then((response) => {
              setWeather(response.data);
              setQuery(""); // Reset the input field
            })
            .catch((error) => {
              console.error("Error fetching weather data:", error);
              setWeather("");
              setQuery("");
              setError({ message: "Weather Data Not Found", query: location });
            });
        } else {
          setError({ message: "Location Not Found", query: location });
        }
      })
      .catch((error) => {
        console.error("Error fetching location data:", error);
        setError({ message: "Location Not Found", query: location });
      });
  };

  useEffect(() => {
    search("Delhi"); // Default city
  }, []);

  return (
    <div className="forecast">
      <div className="forecast-icon">
        <ReactAnimatedWeather
          icon="CLEAR_DAY"
          color="white"
          size={112}
          animate={true}
        />
      </div>
      <div className="today-weather">
        <h3>Today's Weather</h3>
        <div className="search-box">
          <input
            type="text"
            className="search-bar"
            placeholder="Search any city or village"
            onChange={(e) => setQuery(e.target.value)}
            value={query}
          />
          <div className="img-box">
            <img
              src="https://images.avishkaar.cc/workflow/newhp/search-white.png"
              onClick={() => search(query)}
              alt="Search"
            />
          </div>
        </div>
        <ul>
          {typeof weather.main != "undefined" ? (
            <div>
              <li className="cityHead">
                <p>
                  {weather.name}, {weather.sys.country}
                </p>
                <img
                  className="temp"
                  src={`https://openweathermap.org/img/wn/${weather.weather[0].icon}.png`}
                  alt="Weather Icon"
                />
              </li>
              <li>
                Temperature{" "}
                <span className="temp">
                  {Math.round(weather.main.temp)}°c ({weather.weather[0].main})
                </span>
              </li>
              <li>
                Humidity{" "}
                <span className="temp">{Math.round(weather.main.humidity)}%</span>
              </li>
              <li>
                Wind Speed{" "}
                <span className="temp">{Math.round(weather.wind.speed)} Km/h</span>
              </li>
            </div>
          ) : (
            <li>
              {error.query} {error.message}
            </li>
          )}
        </ul>
      </div>
    </div>
  );
}

export default Forcast;
